
const JD_API_HOST = 'https://api.m.jd.com/client.action';

const newShareCodesPk = [
    "IgNWdiLGaPaAvmHOAAaovCBNL6kvOaPHoljqjwT8SF-bKmdB1rtTOSneZGWwCGyt"
];

let CookieJDs = process.env.JD_COOKIE.split('&');
const cookiesArr = [...new Set(CookieJDs.filter(item => item !== "" && item !== null && item !== undefined))];

let ua = null;
let secretp = null;

(async ()=>{
    for(let cookie in cookiesArr){
        if (!cookie) {
            continue
        }
        ua = initUserAgents();
        const UserName = decodeURIComponent(cookie.match(/pt_pin=(.+?);/) && cookie.match(/pt_pin=(.+?);/)[1]);
        console.log(`******开始京东账号${UserName}*********`);
        const {error, nikname} = await TotalBean(cookie);
        if(error || nikname == ""){
            console.log(error);
            continue;
        }
        await getHomeData();

        // helpFriendsPK
        await helpFriendsPK()

        for (let code of newShareCodesPk) {
            if (!code) continue
            console.log(`${nikname}去助力PK好友:${code}`);
            if(!await pkAssignGroup(cookie, code)){
                break;
            }
            await deylayExecute(12345);
        }
    }
})()

function deylayExecute(second){
    return new Promise(resolve =>{
        setTimeout(() => {
            return resolve();
        }, second);
    })
}

function encode(data, aa, extraData) {
    const temp = {
        "extraData": JSON.stringify(extraData),
        "businessData": JSON.stringify(data),
        "secretp": aa,
    }
    return { "ss": (JSON.stringify(temp)) };
}

function pkAssignGroup(cookie, inviteId) {
    let temp = {
        "confirmFlag": 1,
        "inviteId": inviteId,
    }
    const extraData = {
        "jj": 6,
        "buttonid": "jmdd-react-smash_0",
        "sceneid": "homePageh5",
        "appid": '50073'
    }
    let body = {
        ...encode(temp, secretp, extraData),
        inviteId: inviteId
    }
    return new Promise(resolve => {
        let options = taskPostUrl("nian_pk_assistGroup", body, "nian_pk_assistGroup");
        headers.Cookie = cookie;
        $.post(options, async (err, resp, data) => {
            let canhelp = true;
            try {
                if (err) {
                    console.log(`${JSON.stringify(err)}`)
                    console.log(`${$.name} API请求失败，请检查网路重试`)
                } else {
                    if (safeGet(data)) {
                        data = JSON.parse(data);
                        if (data.data && data.data.bizMsg) {
                            console.log(data.data.bizMsg)
                        } else {
                            console.log(`助力失败，未知错误:${JSON.stringify(data)}`)
                            canhelp = false;
                        }
                    }
                }
            } catch (e) {
                $.logErr(e, resp)
            } finally {
                resolve(canhelp);
            }
        })
    })
}

function taskPostUrl(function_id, body = {}, function_id2) {
    let url = `${JD_API_HOST}`;
    if (function_id2) {
        url += `?functionId=${function_id2}`;
    }
    return {
        url,
        body: `functionId=${function_id}&body=${escape(JSON.stringify(body))}&client=wh5&clientVersion=1.0.0`,
        headers: {
            "Cookie": null,
            "origin": "https://h5.m.jd.com",
            "referer": "https://h5.m.jd.com/",
            'Content-Type': 'application/x-www-form-urlencoded',
            "User-Agent": ua
        }
    }
}

function TotalBean(cookie) {
    return new Promise(resolve => {
        const options = {
            "url": `https://wq.jd.com/user/info/QueryJDUserInfo?sceneval=2`,
            "headers": {
                "Accept": "application/json,text/plain, */*",
                "Content-Type": "application/x-www-form-urlencoded",
                "Accept-Encoding": "gzip, deflate, br",
                "Accept-Language": "zh-cn",
                "Connection": "keep-alive",
                "Cookie": cookie,
                "Referer": "https://wqs.jd.com/my/jingdou/my.shtml?sceneval=2",
                "User-Agent": ua
            }
        }
        $.post(options, (err, resp, data) => {
            let ret = {
                error: "",
                nikname: "",
            };
            try {
                if (err) {
                    ret.error = `API请求失败，请检查网路重试: ${JSON.stringify(err)}`;
                } else {
                    if (data) {
                        data = JSON.parse(data);
                        if (data['retcode'] === 13) {
                            ret.error = "cookie过期";
                        }else{
                            ret.nickname = data['base'].nickname;
                        }
                    } else {
                        ret.error = `京东服务器返回空数据`;
                    }
                }
            } catch (e) {
                ret.error = e.message;
                console.log(e, resp);
            } finally {
                resolve(ret);
            }
        })
    })
}


function getHomeData() {
    return new Promise((resolve) => {
        $.post(taskPostUrl('nian_getHomeData'), (err, resp, data) => {
            try {
                if (err) {
                    console.log(`${JSON.stringify(err)}`)
                    console.log(`${$.name} API请求失败，请检查网路重试`)
                } else {
                    data = JSON.parse(data);
                    if (data && data.data['bizCode'] === 0) {
                        secretp = data.data.result.homeMainInfo.secretp;
                        if (!secretp) {
                            console.log(`账号被风控`)
                            message += `账号被风控，无法参与活动\n`
                            secretp = null
                            return
                        }
                    } else {
                        secretp = null
                        console.log(`账号被风控，无法参与活动`)
                    }
                }
            } catch (e) {
                console.log(e, resp);
            } finally {
                resolve();
            }
        })
    })
}


function safeGet(data) {
    try {
        if (typeof JSON.parse(data) == "object") {
            return true;
        }
    } catch (e) {
        console.log(e);
        console.log(`京东服务器访问数据为空，请检查自身设备网络情况`);
        return false;
    }
}

